import { useNavigate } from 'react-router-dom';
// import { uuid4} from 'uuid4'

import classes from './EventForm.module.css';


function EventForm({ method, event, value }) {
  const navigate = useNavigate();
  function cancelHandler() {
    navigate('..',{
      relative: 'path'
    });
  }

  return (
    <form action='http://localhost:8080/events' method={method} className={classes.form} onSubmit={(e) => event(e)}>
      <p>
        <label htmlFor="title">Title</label>
        <input id="title" type="text" name="title" value={value.title || null} required />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input id="image" type="url" name="image" value={value.title || null} required />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input id="date" type="date" name="date" value={value.title || null} required />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea id="description" name="description" value={value.title || null} rows="5" required />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler}>
          Cancel
        </button>
        <button type='submit'>Save</button>
      </div>
    </form>
  );
}

export default EventForm;
